import React, { useState, useEffect } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import EmailIcon from '@material-ui/icons/Email';
import PictureAsPdfIcon from '@material-ui/icons/PictureAsPdf';
import Portfolio from './projectCards';
import HttpIcon from '@material-ui/icons/Http';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Zachary Nylen
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
}));

export default function Album() {
  const classes = useStyles();

  const [projects, setProjects] = useState(Portfolio)

  return (
    <React.Fragment>
      <CssBaseline />
      <AppBar style={{ backgroundColor: "#00695D" }} position="relative">
        <Toolbar>
          <Link color="inherit" href="https://github.com/znylen88"><GitHubIcon className={classes.icon}></GitHubIcon></Link>
          <Link color="inherit" href="https://www.instagram.com/zachy_d/?hl=en"><InstagramIcon className={classes.icon}></InstagramIcon></Link>
          <Link color="inherit" href="https://www.linkedin.com/in/zachary-nylen-52265b192/"><LinkedInIcon className={classes.icon}></LinkedInIcon></Link>
        </Toolbar>
      </AppBar>
      <main>
        {/* Hero unit */}
        <div className={classes.heroContent}>
          <Container maxWidth="sm">
            <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
              Zachary Nylen
            </Typography>
            <Typography variant="h5" align="center" color="textSecondary" paragraph>
              <LocationOnIcon />&nbsp;San Diego, CA
            </Typography>
            <div className={classes.heroButtons}>
              <Grid container spacing={2} justify="center">
                <Grid item>
                  <Button style={{ backgroundColor: "#00695D" }} variant="contained" color="primary">
                    <EmailIcon />&nbsp;&nbsp;Contact
                  </Button>
                </Grid>
                <Grid item>
                  <Button style={{ color: "#00695D" }} variant="outlined" color="default">
                    <PictureAsPdfIcon />&nbsp;&nbsp;Resume
                  </Button>
                </Grid>
              </Grid>
            </div>
          </Container>
        </div>
        <Container className={classes.cardGrid} maxWidth="md">
          {/* End hero unit */}
          <Grid container spacing={4}>
            {projects.map(result => (
              <Grid item key={result} xs={12} sm={6} md={4}>
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.cardMedia}
                    image="https://source.unsplash.com/random"
                    title="Image title"
                  />
                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                      {result.name}
                    </Typography>
                    <Typography>
                      {result.description}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Link href={result.githubLink}>
                      <GitHubIcon size="small" style={{ color: "black" }} />
                    </Link>
                    <Link href={result.deployed}>
                      <HttpIcon style={{ color: "black" }} />
                    </Link>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          "It's not that we use technology, we live technology." - Godfrey Reggio
        </Typography>
        <br></br>
        <Copyright />
      </footer>
      {/* End footer */}
    </React.Fragment>
  );
}
